const plugin = {
  id: 'elint-plugin-esm',
  name: 'esm',
  type: 'formatter',
  activateConfig: {
    extensions: ['.js']
  },
  async execute(text) {
    return {
      source: text,
      output: text,
      errorCount: 0,
      warningCount: 0
    }
  },
  getVersion() {
    return {
      version: '1.0.0'
    }
  }
}

export default plugin